import React from 'react'
import Loading from './Loading'

class FunEvents extends React.Component {
  constructor() {
    super()

    this.state = {
      isLoading: true,
      funEvents: []
    }
  }

  componentDidMount() {
    fetch('/json/fun-events.json')
      .then(res => {
        if (res.status === 200) return res.json()
        return []
      })
      .then(json => {
        this.setState({ funEvents: json, isLoading: false })
      })
      .catch(err => {
        console.log(err)
      })
  }

  render() {
    const { isLoading, funEvents } = this.state
    return isLoading ? (
      <Loading />
    ) : (
      funEvents.map(funEvent => {
        return <FunEvent data={funEvent} key={funEvent.img_url} />
      })
    )
  }
}

/**
 * Single Fun Event UI Component
 * @param {*} props
 */
const FunEvent = props => {
  const { data } = props
  return (
    <div>
      <figure className="image">
        <img
          className="has-text-centered"
          src={data.img_url}
          alt={data.img_caption}
        />
      </figure>
      <div>Image Caption: {data.img_caption}</div>
      <div>Link for Image: {data.img_link}</div>
    </div>
  )
}

export default FunEvents
