import React from 'react'
/* import Loading from './Loading' */
import Slider from 'react-slick'
import { VIDEO_THUMB } from '../../constants/SiteElements'

class FunEvents extends React.Component {
  render() {
    var settings = {
      dots: true
    }
    return (
      <div className="container">
        <Slider {...settings}>
          <div>
            <img src={VIDEO_THUMB} />
          </div>
        </Slider>
      </div>
    )
  }
}

