import React from 'react'
import {
  faFacebookSquare,
  faLinkedinIn
} from '@fortawesome/free-brands-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const socialLinks = [
  {
    name: 'Facebook',
    icon: faFacebookSquare,
    link: 'https://www.facebook.com/LifeatFLIP/',
    icon_id: 'socialFb'
  },
  {
    name: 'LinkedIn',
    icon: faLinkedinIn,
    link: 'https://www.linkedin.com/showcase/careers-at-flip/',
    icon_id: 'socialLi'
  }
]

const Footer = () => {
  const htmlSocialIcons = socialLinks.map(socialLink => {
    return <SocialIcon key={socialLink.name} data={socialLink} />
  })
  return (
    <footer className="footer is-bg-flip-blue">
      <div className="columns">
        <div className="column">
          <p className="is-left has-text-center-touch has-text-white">
            &copy; {new Date().getFullYear()} Finitiatives Learning India Pvt.
            Ltd.
          </p>
        </div>
        <div className="column">
          <p className="buttons has-text-center-touch has-text-right">
            {htmlSocialIcons}
          </p>
        </div>
      </div>
    </footer>
  )
}

const SocialIcon = props => {
  const data = props.data
  const socialName = data.name
  const socialLink = data.link
  const socialIcon = data.icon
  const socialIconId = data.icon_id
  return (
    <a
      href={socialLink}
      target="_blank"
      rel="noopener noreferrer"
      className="button is-link is-inverted"
    >
      <span classNameicon="icon">
        <FontAwesomeIcon icon={socialIcon} id={socialIconId} className="fab" />
      </span>
      {socialName}
      <span />
    </a>
  )
}

export default Footer
